graphql.query:
query getEventBySlug($slug: String!) {
  eventList(filter: {
    slug: {
      _expressions: [ { value: $slug } ]
    }
  	}) {
    items {
      _path
      eventName
      slug
      eventStart
      eventEnd
      description {
        json
      }
      speakers {
        ...on EventSpeakerModel {
          __typename
          _path
          name
        	title
          profilePicture {
            ...on ImageRef {
              _path
              _authorUrl
            	_publishUrl  
            }
          }
        }
        ...on KeynoteSpeakerModel {
          __typename
          _path
          name
        	title
          heroImage {
            ...on ImageRef {
              _path
              _authorUrl
            	_publishUrl  
            }
          }
        }
      }
      teasingImage {
        ...on ImageRef {
          _path
          _authorUrl
          _publishUrl
          width
          height
        }
      }
    }
  }
}

